import React from 'react';
import styled from 'styled-components';

import Room1 from '../components/room1.js';
import Room2 from '../components/room2.js';
import Room3 from '../components/room3.js';
import Room4 from '../components/room4.js';

const GridItem = styled.div`
	position: relative;
	display: inline-block;
  	}
`;

const Item = styled.div`	
	position: relative;
	display: flex;
	flex-basis: calc(15% - 4rem);
	flex-grow: 1;
	flex-shrink: 0;
	margin: 0 auto;
	background: white;
	padding-bottom: 11px;
	padding-left: 11px;
	padding-right: 11px;
	padding-top: 11px
`;


const ItemWrap = styled.div`	
	margin-left: 7px;
	padding-top: 3px;
	padding-bottom: 3px;
	padding-left: 3px;
	padding-right: 3px;
	background: #EAEAEA;
	max-width: 180px;
	max-height: 200px
	border-radius: 5px;
`;

const Box = styled.div`
	position: relative;
	display: inline-block;
`;

const Slot = styled.div`
	margin: 7px
`;
const Heading = styled.span`
	font-weight: bold;
	font-size: 15px;
	margin-left: 5px;
`;
const P = styled.p`
	margin: 1px 0;
	font-size: 12px;
`;


class Parent extends React.Component {

	constructor(props){

	    super(props)
	    if(this.props.peter !== null){;
			let initial = this.props.peter;
			this.state = {
				// checked: initial.checked,
				// adults1: initial.adults1,
				// children1: initial.children1,
				// adults2: initial.adults2,
				// children2: initial.children2,
				// adults3: initial.adults3,
				// children3: initial.children3,
				// adults4: initial.adults4,
				// children4: initial.children4
			}
		}
		else{
			this.state = {
				checked: '',
				adults1: 1,
				children1: 0,
				adults2: 1,
				children2: 0,
				adults3: 1,
				children3: 0,
				adults4: 1,
				children4: 0
			}
		}	   
	}

	state = {

	}

		render() {

		let select = '';
		let optionsAdults = '';
		let optionsChildren = '';

		if(this.state.check===true && this.props.data !== undefined){
			console.log('true');
			select = 
				<ItemWrap>
					<Heading2>Room {this.state.room}</Heading2>
						<Item>
						<Checkbox type='checkbox' onClick={() => this.getStuff()} checked/>
							<Slot>
								<P>Adults</P>
								<P>(18+)</P>
								<div>
								<select id='selectionAdults2' value={this.props.data.adults2} onChange={this.twoCallsAdult2}>
								<option>{1}</option>
								<option>{2}</option>
								</select>
								</div>
							</Slot>
							<Slot>
								<P>Children</P>
								<P>(0-17)</P>
								<select id='selectionChildren2' value={this.props.data.children2} onChange={this.twoCallsChildren2}>
								<option>{0}</option>
								<option>{1}</option>
								<option>{2}</option>
								</select>
							</Slot>
						</Item>
				</ItemWrap>
		}
		else{
			select = 
				<ItemWrapNay>
					<Heading2Nay>Room {this.state.room}</Heading2Nay>
						<ItemNay>
							<Checkbox type='checkbox' onClick={() => this.getStuff()} />

									<Slot>
										<P>Adults</P>
										<P>(18+)</P>

										<select disabled>
										<option>{1}</option>
										<option>{2}</option>
										</select>

									</Slot>
									<Slot>
										<P>Children</P>
										<P>(0-17)</P>
										<select disabled>
										<option>{0}</option>
										<option>{1}</option>
										<option>{2}</option>
										</select>
									</Slot>

						</ItemNay>
				</ItemWrapNay>
		}

		return (
			<GridItem>
				<Box>
					{select}
				</Box>
			</GridItem>
		);
	}

	storeStuff(){
		let status = this.state
		localStorage.setItem('submittedData', JSON.stringify(status));
	}

	eraseStuff(){
		localStorage.clear();
	}

}

export default Parent;
