import React from 'react';
import styled from 'styled-components';

import Room1 from '../components/room1.js';
import Room2 from '../components/room2.js';
import Room3 from '../components/room3.js';
import Room4 from '../components/room4.js';

const GridItem = styled.div`
	position: relative;
	display: inline-block;
  	}
`;

const Item = styled.div`	
	position: relative;
	display: flex;
	flex-basis: calc(15% - 4rem);
	flex-grow: 1;
	flex-shrink: 0;
	margin: 0 auto;
	background: white;
	padding-bottom: 11px;
	padding-left: 11px;
	padding-right: 11px;
	padding-top: 11px
`;


const ItemWrap = styled.div`	
	margin-left: 7px;
	padding-top: 3px;
	padding-bottom: 3px;
	padding-left: 3px;
	padding-right: 3px;
	background: #EAEAEA;
	max-width: 180px;
	max-height: 200px
	border-radius: 5px;
`;

const Box = styled.div`
	position: relative;
	display: inline-block;
`;

const Slot = styled.div`
	margin: 7px
`;
const Heading = styled.span`
	font-weight: bold;
	font-size: 15px;
	margin-left: 5px;
`;
const P = styled.p`
	margin: 1px 0;
	font-size: 12px;
`;


class Parent extends React.Component {

	constructor(props){

	    super(props)
	    if(this.props.peter !== null){;
			let initial = this.props.peter;
			this.state = {
				// checked: initial.checked,
				// adults1: initial.adults1,
				// children1: initial.children1,
				// adults2: initial.adults2,
				// children2: initial.children2,
				// adults3: initial.adults3,
				// children3: initial.children3,
				// adults4: initial.adults4,
				// children4: initial.children4
			}
		}
		else{
			this.state = {
				checked: '',
				adults1: 1,
				children1: 0,
				adults2: 1,
				children2: 0,
				adults3: 1,
				children3: 0,
				adults4: 1,
				children4: 0
			}
		}	   
	}

	state = {

	}

		render() {

		let select = '';
		let optionsAdults = '';
		let optionsChildren = '';

		if(this.props.data !== undefined){
			console.log('true');
			select = 
				<ItemWrap>
					<Heading>Room {this.state.room}</Heading>
						<Item>
							<Slot>
								<P>Adults</P>
								<P>(18+)</P>
								<div>
								<select id='selectionAdults' value={this.props.data.adults} onChange={this.twoCallsAdult}>
								<option>{1}</option>
								<option>{2}</option>
								</select>
								</div>
							</Slot>
							<Slot>
								<P>Children</P>
								<P>(0-17)</P>
								<select id='selectionChildren' value={this.props.data.children} onChange={this.twoCallsChildren}>
								<option>{0}</option>
								<option>{1}</option>
								<option>{2}</option>
								</select>
							</Slot>
						</Item>
				</ItemWrap>
		}

		return (
			<GridItem>
				<Box>
					{select}
				</Box>
			</GridItem>
		);
	}

	twoCallsAdult = e => {
		e.persist();
  		this.functionOneAdult(e)
  		this.functionTwoAdult(e)
	}

	functionOneAdult(e){
		e.persist();
		this.setState({adults: parseInt(e.target.value)}, function (){
			this.props.fromChildToParentCallback([this.state.check, this.state.room, this.state.adults, this.state.children]);
		})
	}

	functionTwoAdult(e){
		e.persist();
		document.getElementById('selectionAdults').value = parseInt(e.target.value);
		if(this.props.data!==undefined){
			this.props.data.adults1 = parseInt(e.target.value);
		}
	}

	twoCallsChildren = e => {
		e.persist();
  		this.functionOneChildren(e)
  		this.functionTwoChildren(e)
	}

	functionOneChildren(e){
		e.persist();
		this.setState({children: parseInt(e.target.value)}, function () {
			this.props.fromChildToParentCallback([this.state.check, this.state.room, this.state.adults, this.state.children]);
		})
	}

	functionTwoChildren(e){
		e.persist();
		if (process.browser){
			document.getElementById('selectionChildren').value = parseInt(e.target.value);
			if(this.props.data!==undefined){
				this.props.data.children1 = parseInt(e.target.value);
			}
		}
	}

	componentDidMount(){
		this.getStuff();
	}

	componentWillReceiveProps(nextProps) {
  		if(this.props != nextProps) {
  			if(this.props.data!==undefined){
    			this.setState({check: nextProps.check, adults: this.props.data.adults1, children: this.props.data.children1});
    		}
    		else{
    			this.setState({check: nextProps.check, adults: this.state.adults, children: this.state.children});
    		}
  		}
	}

	getStuff(){
		this.setState({check: !this.state.check, adults: this.state.adults || 1, children:this.state.children}, function () {
			if(this.props.data!==undefined){
				console.log('titan');
				this.props.fromChildToParentCallback([this.state.check, this.state.room, this.props.data.adults1, this.props.data.children1]);
			}
			else{
				console.log('eh');
				this.props.fromChildToParentCallback([this.state.check, this.state.room, this.state.adults, this.state.children]);
			}
        });
	}

}

export default Parent;
